mapper.select:
SELECT * FROM movie
ORDER BY id
    LIMIT #{offset}, #{limit}

mapper.insert:
INSERT INTO movie (
    name, chinese_name, release_year, type, language,
    duration, poster_url, description, imdb_link
) VALUES (
             #{name}, #{chineseName}, #{releaseYear}, #{type}, #{language},
             #{duration}, #{posterUrl}, #{description}, #{imdbLink}
         )
